import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import './EmployeeEdit.css';
import { getEmployee, updateEmployee, createEmployee } from '../utils/api';

const EmployeeEdit = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [employee, setEmployee] = useState({
    name: '',
    email: '',
    mobile: '',
    designation: '',
    gender: '',
    course: [],
    image: '',
  });
  const [isEditing, setIsEditing] = useState(false);

  useEffect(() => {
    if (id) {
      const fetchEmployee = async () => {
        try {
          const data = await getEmployee(id);
          setEmployee(data);
          setIsEditing(true);
        } catch (error) {
          console.error('Failed to fetch employee', error);
        }
      };

      fetchEmployee();
    }
  }, [id]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (isEditing) {
        await updateEmployee(id, employee);
      } else {
        await createEmployee(employee);
      }
      navigate('/employee-list');
    } catch (error) {
      console.error('Failed to save employee', error);
    }
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;

    if (type === 'checkbox') {
      setEmployee((prev) => ({
        ...prev,
        course: checked
          ? [...prev.course, value]
          : prev.course.filter((course) => course !== value),
      }));
    } else if (type === 'file') {
      setEmployee((prev) => ({
        ...prev,
        image: e.target.files[0],
      }));
    } else {
      setEmployee((prev) => ({
        ...prev,
        [name]: value,
      }));
    }
  };

  return (
    <div className="employee-edit-container">
      <h2>{isEditing ? 'Edit Employee' : 'Create Employee'}</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Name</label>
          <input type="text" name="name" value={employee.name} onChange={handleChange} required />
        </div>
        <div>
          <label>Email</label>
          <input type="email" name="email" value={employee.email} onChange={handleChange} required />
        </div>
        <div>
          <label>Mobile No</label>
          <input type="text" name="mobile" value={employee.mobile} onChange={handleChange} required />
        </div>
        <div>
          <label>Designation</label>
          <select name="designation" value={employee.designation} onChange={handleChange} required>
            <option value="HR">HR</option>
            <option value="Manager">Manager</option>
            <option value="Sales">Sales</option>
          </select>
        </div>
        <div>
          <label>Gender</label>
          <label>
            <input type="radio" name="gender" value="Male" checked={employee.gender === 'Male'} onChange={handleChange} />
            Male
          </label>
          <label>
            <input type="radio" name="gender" value="Female" checked={employee.gender === 'Female'} onChange={handleChange} />
            Female
          </label>
        </div>
        <div>
          <label>Course</label>
          <label>
            <input type="checkbox" name="course" value="MCA" checked={employee.course.includes('MCA')} onChange={handleChange} />
            MCA
          </label>
          <label>
            <input type="checkbox" name="course" value="BCA" checked={employee.course.includes('BCA')} onChange={handleChange} />
            BCA
          </label>
          <label>
            <input type="checkbox" name="course" value="BSC" checked={employee.course.includes('BSC')} onChange={handleChange} />
            BSC
          </label>
        </div>
        <div>
          <label>Image</label>
          <input type="file" name="image" onChange={handleChange} />
        </div>
        <button type="submit">{isEditing ? 'Update' : 'Create'}</button>
      </form>
    </div>
  );
};

export default EmployeeEdit;
