// src/components/CreateEmployee.js

import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './CreateEmployee.css';
import { createEmployee } from '../api';

const CreateEmployee = () => {
  const [formData, setFormData] = useState({
    f_Name: '',
    f_Email: '',
    f_Mobile: '',
    f_Designation: '',
    f_gender: '',
    f_Course: [],
    f_Image: '',
  });
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;

    if (type === 'checkbox') {
      setFormData((prevData) => ({
        ...prevData,
        [name]: checked
          ? [...prevData[name], value]
          : prevData[name].filter((item) => item !== value),
      }));
    } else {
      setFormData({ ...formData, [name]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Client-side validation
    if (!validateForm()) return;

    try {
      await createEmployee(formData);
      console.log('Employee created successfully');
      navigate('/employee-list');
    } catch (error) {
      console.error('Error creating employee:', error);
      setError('Failed to create employee.');
    }
  };

  const validateForm = () => {
    const { f_Name, f_Email, f_Mobile, f_Designation, f_gender, f_Course } = formData;
    if (!f_Name || !f_Email || !f_Mobile || !f_Designation || !f_gender || f_Course.length === 0) {
      setError('All fields are required.');
      return false;
    }

    // Email validation
    const emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailPattern.test(f_Email)) {
      setError('Invalid email format.');
      return false;
    }

    // Numeric validation for mobile number
    if (!/^\d+$/.test(f_Mobile)) {
      setError('Mobile number must be numeric.');
      return false;
    }

    // Validate file extension
    const imageExtension = formData.f_Image.split('.').pop();
    if (!['jpg', 'jpeg', 'png'].includes(imageExtension)) {
      setError('Only jpg/png files are allowed.');
      return false;
    }

    return true;
  };

  return (
    <div className="create-employee-container">
      <h2>Create Employee</h2>
      {error && <div className="error">{error}</div>}
      <form onSubmit={handleSubmit}>
        <div>
          <label>Name</label>
          <input
            type="text"
            name="f_Name"
            value={formData.f_Name}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Email</label>
          <input
            type="email"
            name="f_Email"
            value={formData.f_Email}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Mobile No</label>
          <input
            type="text"
            name="f_Mobile"
            value={formData.f_Mobile}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Designation</label>
          <select
            name="f_Designation"
            value={formData.f_Designation}
            onChange={handleChange}
            required
          >
            <option value="">Select</option>
            <option value="Manager">Manager</option>
            <option value="Developer">Developer</option>
            <option value="Designer">Designer</option>
            <option value="Tester">Tester</option>
          </select>
        </div>
        <div>
          <label>Gender</label>
          <div>
            <label>
              <input
                type="radio"
                name="f_gender"
                value="Male"
                checked={formData.f_gender === 'Male'}
                onChange={handleChange}
                required
              />
              Male
            </label>
            <label>
              <input
                type="radio"
                name="f_gender"
                value="Female"
                checked={formData.f_gender === 'Female'}
                onChange={handleChange}
                required
              />
              Female
            </label>
            <label>
              <input
                type="radio"
                name="f_gender"
                value="Other"
                checked={formData.f_gender === 'Other'}
                onChange={handleChange}
                required
              />
              Other
            </label>
          </div>
        </div>
        <div>
          <label>Course</label>
          <div>
            <label>
              <input
                type="checkbox"
                name="f_Course"
                value="Course1"
                checked={formData.f_Course.includes('Course1')}
                onChange={handleChange}
              />
              Course 1
            </label>
            <label>
              <input
                type="checkbox"
                name="f_Course"
                value="Course2"
                checked={formData.f_Course.includes('Course2')}
                onChange={handleChange}
              />
              Course 2
            </label>
            <label>
              <input
                type="checkbox"
                name="f_Course"
                value="Course3"
                checked={formData.f_Course.includes('Course3')}
                onChange={handleChange}
              />
              Course 3
            </label>
          </div>
        </div>
        <div>
          <label>Image URL</label>
          <input
            type="text"
            name="f_Image"
            value={formData.f_Image}
            onChange={handleChange}
          />
        </div>
        <button type="submit">Create Employee</button>
      </form>
    </div>
  );
};

export default CreateEmployee;
