import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './EmployeeList.css';
import { fetchEmployees } from '../api';

const EmployeeList = () => {
  const [employees, setEmployees] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    const loadEmployees = async () => {
      try {
        const data = await fetchEmployees();
        console.log('Fetched employees:', data);
        setEmployees(data);
      } catch (error) {
        console.error('Error fetching employees:', error);
        setError('Failed to load employees');
      }
    };

    loadEmployees();
  }, []);

  return (
    <div className="employee-list-container">
      <div className="employee-list-header">
        <h2>Employee List</h2>
        <div className="header-actions">
          <span className="employee-count">Number of Employees: {employees.length}</span>
          <Link to="/employee-create" className="create-employee-button">Create Employee</Link>
        </div>
      </div>
      {error && <div className="error">{error}</div>}
      {employees.length === 0 ? (
        <p>No employees found.</p>
      ) : (
        <table>
          <thead>
            <tr>
              <th>Unique Id</th>
              <th>Image</th>
              <th>Name</th>
              <th>Email</th>
              <th>Mobile No</th>
              <th>Designation</th>
              <th>Gender</th>
              <th>Course</th>
              <th>Create Date</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {employees.map((emp) => (
              <tr key={emp._id}>
                <td>{emp._id}</td>
                <td><img src={emp.f_Image} alt={emp.f_Name} /></td>
                <td>{emp.f_Name}</td>
                <td>{emp.f_Email}</td>
                <td>{emp.f_Mobile}</td>
                <td>{emp.f_Designation}</td>
                <td>{emp.f_gender}</td>
                <td>{emp.f_Course}</td>
                <td>{new Date(emp.f_Createdate).toLocaleDateString()}</td>
                <td>
                  <Link to={`/employee-edit/${emp._id}`}>Edit</Link> - <button onClick={() => { /* Handle Delete */ }}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default EmployeeList;
